def condition(a, b, op):
  # op: 0:a>b, 1:a<b, 2:a==b, 3:a!=b, 4:a<=b, 5:a>=b
  if op == 0:
    return 1 if a > b else 0
  elif op == 1:
    return 1 if a < b else 0
  elif op == 2:
    return 1 if a == b else 0
  elif op == 3:
    return 1 if a != b else 0
  elif op == 4:
    return 1 if a <= b else 0
  elif op == 5:
    return 1 if a >= b else 0
  return 0
